import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent
{
  title = 'Biblical Bestiary';
  version = "4.0";

  constructor(private router: Router)
  {

  }

  public displayVersion()
  {
    alert(this.title + " Version: " + this.version);
  }

  public displayCreatureList()
  {
    this.router.navigate(['list-creatures'], { queryParams: { data: new Date()} });
  }
}
<div class="container">

    <h1>Welcome to {{ title }}</h1>


    <nav class="navbar navbar-light navbar-expand-md navigation-clean-button">

    <div class="d-flex justify-content-center" id="navbarNav">
      <div style="margin: 0 auto;">
      <div class="navbar-nav">
        <a class="submit-button text-center" href="#">Biblical Bestiary</a>
        <a class="submit-button text-center" href="https://www.gcu.edu" target="_blank">GCU Home Page</a>
        <a class="submit-button text-center" routerLink="/create">Create Creature</a>
        <a class="submit-button text-center" (click)="displayCreatureList()">Creature List</a>
        <button class="submit-button text-center" (click)="displayVersion()">About</button>
      </div>
    </div>
    </div>
  </nav>

  <router-outlet></router-outlet>

</div>
